define(["jquery"],function(t){t(".loginbtn").on("click",function(){var r=t.trim(t(".user").val()),e=t.trim(t(".pwd").val()),s="";""==r?s="！请输入账号":""==e&&(s="！请输入密码"),s?t(".err").html(s):t.ajax({url:"/login",type:"post",dataType:"json",data:{user:r,pwd:e},success:function(r){console.log(r),"success"==r.result?history.go(-1):"errors"==r.result&&(s="密码或用户名错误！",t(".err").html(s))}})})});